mapper.delete:
update bs_attachment
set att_source_type = CONCAT(att_source_type,'_deleted')
where id = #{id,jdbcType=BIGINT}

mapper.update:
update bs_attachment set att_id = #{attId,jdbcType=BIGINT}, status = #{status,jdbcType=INTEGER}, att_create_uuid = #{attCreateUuid,jdbcType=VARCHAR}, att_name = #{attName,jdbcType=VARCHAR}, att_ext = #{attExt,jdbcType=VARCHAR}, att_path = #{attPath,jdbcType=VARCHAR}, att_abspath = #{attAbspath,jdbcType=VARCHAR}, att_protocol = #{attProtocol,jdbcType=VARCHAR}, att_context = #{attContext,jdbcType=VARCHAR}, att_source_type = #{attSourceType,jdbcType=VARCHAR}, att_source_id = #{attSourceId,jdbcType=BIGINT}, att_source_uuid = #{attSourceUuid,jdbcType=CHAR}, att_file_type = #{attFileType,jdbcType=VARCHAR}, att_extends1 = #{attExtends1,jdbcType=INTEGER}, att_extends2 = #{attExtends2,jdbcType=VARCHAR}, att_extends3 = #{attExtends3,jdbcType=DATE}, update_time = now(), dm_desc = #{dmDesc,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
where uuid = #{uuid,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select
from bs_attachment
order by id DESC limit #{pageStart,jdbcType=BIGINT}, #{pageSize,jdbcType=INTEGER}
