mapper.delete:
update bs_mail
set status = 2
where id = #{id,jdbcType=BIGINT}

mapper.update:
update bs_mail set update_time = now(), status = #{status,jdbcType=INTEGER}, to_email = #{toEmail,jdbcType=VARCHAR}, mail_subject = #{mailSubject,jdbcType=VARCHAR}, mail_content = #{mailContent,jdbcType=VARCHAR}, mail_type = #{mailType,jdbcType=INTEGER}, mail_result = #{mailResult,jdbcType=VARCHAR}, dm_desc = #{dmDesc,jdbcType=VARCHAR}, dm_desc2 = #{dmDesc2,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}
where uuid = #{uuid,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select
from bs_mail
order by id DESC limit #{pageStart,jdbcType=BIGINT}, #{pageSize,jdbcType=INTEGER}
