set 
		update_time = now(),
		status = #{status,jdbcType=INTEGER},
		dm_desc = #{dmDesc,jdbcType=VARCHAR},
		dm_desc2 = #{dmDesc2,jdbcType=VARCHAR},
		visit_count = #{visitCount,jdbcType=VARCHAR}


where id = #{id,jdbcType=BIGINT}


where uuid = #{uuid,jdbcType=VARCHAR}


where id = #{id,jdbcType=BIGINT}
