mapper.delete:
update jg_assets
set status = 2
where id = #{id,jdbcType=BIGINT}

mapper.update:
update jg_assets set update_time = now(), status = #{status,jdbcType=INTEGER}, dm_desc = #{dmDesc,jdbcType=VARCHAR}, dm_desc2 = #{dmDesc2,jdbcType=VARCHAR}, jg_assets_name = #{jgAssetsName,jdbcType=VARCHAR}, jg_assets_position = #{jgAssetsPosition,jdbcType=VARCHAR}, jg_assets_cost = #{jgAssetsCost,jdbcType=DECIMAL}, jg_assets_source = #{jgAssetsSource,jdbcType=VARCHAR}, jg_assets_get_time = #{jgAssetsGetTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=BIGINT}
where uuid = #{uuid,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select
from jg_assets
order by id DESC limit #{pageStart,jdbcType=BIGINT}, #{pageSize,jdbcType=INTEGER}
